import React from 'react'
import { Component } from 'react'
import {Text,View, TextInput, StyleSheet, TouchableOpacity} from 'react-native'
export default class LogInScreen extends React.Component{
    constructor(){
        super()
        this.state = {
            emailId:"",
            passward : ""
        }
    }
    render(){
        return(
            <View>
            <Text>Book Santa App</Text>
            <TextInput
            style = {styles.logInBox}
            placeholder = ''
            keyboardType  = 'email-address'
            onChangeText = {(text)=>{
                this.setState({
                    emailId : text
                })
            }}             
            />
            <TextInput
            style = {styles.logInBox}
            placeholder = ''
            onChangeText = {(text)=>{
                this.setState({
                    passward : text
                })
            }}
            />
            <TouchableOpacity>
                <Text style={{backgroundColor : 'black',width : 300 , height : 50 , justifyContent : 'center',
    borderRadius:25,
    alignItems : 'center',     }}>log in </Text>
            </TouchableOpacity>
            </View>
        )
    }
}
const styles = StyleSheet.create({
    logInBox:{
      width: 300,
      hight:40,
      borderColor : 'black',
      borderBottomWidth2 : 20 ,
        fontSize : 20,
        padding : 10,
        margin: 10,


    },
    textBox : {
    width:300,
    height:50,
    justifyContent : 'center',
    borderRadius:25,
    alignItems : 'center',
    backgroundColor : ' black',
    shadowColor : 'red',

    },
})
